import React, {useState} from "react";
import Modal from "react-modal";

const Bookmark = ({bookmarkTuit}) => {

    const [isOpen, setIsOpen] = useState(false);
    const [modalTuit, setModalTuit] = useState([]);

    function toggleModal() {
        setIsOpen(!isOpen);
        setModalTuit(bookmarkTuit);
      }

  return(
      <>
    <div class="card" onClick={toggleModal} >
    <div class="card-body">
      <p class="card-text">hello world</p>
      <img src="https://www.w3schools.com/bootstrap/newyork.jpg" class="img-rounded" alt="Cinque Terre" width="100" height="100"  style={{height: "15vw", width : "100%", objectFit: "cover"}}/> 
    </div>
  </div>
  <button type="button" class="btn btn-info btn-lg" data-toggle="modal" data-target="#myModal">Open Modal</button>

  <div class="modal fade" id="myModal" role="dialog">
    <div class="modal-dialog">

      <div class="modal-content">
        <div class="modal-header">
          <button type="button" class="close" data-dismiss="modal">&times;</button>
          <h4 class="modal-title">Modal Header</h4>
        </div>
        <div class="modal-body">
          <p>Some text in the modal.</p>
        </div>
        <div class="modal-footer">
          <button type="button" class="btn btn-default" data-dismiss="modal">Close</button>
        </div>
      </div>

    </div>
  </div>

  <Modal
        isOpen={isOpen}
        onRequestClose={toggleModal}
        contentLabel="My dialog"
        className="mymodal"
        overlayClassName="myoverlay"
        closeTimeoutMS={500}
      >
        <div>My modal dialog.</div>
        <button onClick={toggleModal}>Close modal</button>
      </Modal>

  </>
  );
}
export default Bookmark;
