import React, {useEffect, useState} from "react";
import Tuits from "../tuits";
import tuitsArrayJSON from "../tuits/tuits-data.json"
import {useParams} from "react-router-dom";
import {findTuitByUser, createTuit} from '../../services/tuits-service';
import { JsonableValue } from "ts-jest";
const Home = () => {

  const {userId} = useParams();
  const [userEnteredtuit, setUserEnteredtuit] = useState({'text':null})
  const [tuitsArray, setTuitsArray] = useState([]);

  const addTuitForTheUser = () => {

    alert('You added a new tuit');
//6359612e3182d92a93c748b8

   const newTuit = {
      "_id": "123",
      "postedBy": userId,
      "tuit": userEnteredtuit.text,
      "image": "",
      "youtube": null,
      "avatarLogo": "",
      "published": Date.now(),
      "imageOverlay": null,
      "stats": {
        "replies": 0,
        "retuits": 0,
        "likes": 0
      }
    };

    createTuit(userId,newTuit)
    setTuitsArray([newTuit,...tuitsArray])
  }

 useEffect(() => {

  const alreadyPresentTuits=findTuitByUser(userId);
  setTuitsArray([...alreadyPresentTuits,...tuitsArray])

  }, [userId]);

  return(
    <div className="ttr-home">
{userId}
      <div className="border border-bottom-0">
        <h4 className="fw-bold p-2">Home Screen</h4>
        <div className="d-flex">
          <div className="p-2">
            <img className="ttr-width-50px rounded-circle"
                 src="../images/nasa-logo.jpg"/>
          </div>
          <div className="p-2 w-100">
            <textarea
              placeholder="What's happening?"
              className="w-100 border-0" 
              onChangeCapture={(e)=>    setUserEnteredtuit({...userEnteredtuit, 'text' :e.target.value })}></textarea>
            <div className="row">
              <div className="col-10 ttr-font-size-150pc text-primary">
                <i className="fas fa-portrait me-3"></i>
                <i className="far fa-gif me-3"></i>
                <i className="far fa-bar-chart me-3"></i>
                <i className="far fa-face-smile me-3"></i>
                <i className="far fa-calendar me-3"></i>
                <i className="far fa-map-location me-3"></i>
              </div>
              <div className="col-2">
                <a className={`btn btn-primary rounded-pill fa-pull-right
                                fw-bold ps-4 pe-4`} onClick={()=> addTuitForTheUser()}>
                  Tuit
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
       <Tuits tuits={tuitsArray}/> 
    </div>
  );
};
export default Home;
