import React, {useState, useEffect} from "react";
import tuitsArray from "../tuits/tuits-data.json"
import Bookmark from "./bookmark";

function Bookmarks () {

  const [bookmarkedTuits, setBookmarkedTuits]= useState([]);

useEffect(() => {
  //fetch Tuits from an api
  setBookmarkedTuits(tuitsArray);

}, [bookmarkedTuits]);

  return(
    <>
    <div>
    {tuitsArray.map((tuits, index) => (
    index%2===0
      ? (<div class="row">
      <div class="col-6" key={'bookmark-'+index} ><Bookmark  bookmarkTuit={bookmarkedTuits[index]} /></div>
      {index+1<tuitsArray.length?<div class="col-6" key={'bookmark-'+index+1} ><Bookmark bookmarkTuit={bookmarkedTuits[index+1]} /></div>:null}
         </div>)
      : null
  ))}
    </div>
    </>

  );
}
export default Bookmarks;
